import React from "react";
import './header.css';
import {FaShoppingCart} from 'react-icons/fa';
import Globais from "../Globais";



function Header() {

    function handleClick() {
        this.setState({
            mostrarComponente: true
        });
    }

    return(
<div className="header">       
    
    <div className="logos">
        <h1 className="h1Logo">MKS</h1>
        <h3 className="h3Logo">Sistemas</h3>  
        
    </div>
   
    <div className="btn-div">
        <input  className="btn-carrinho"  type="submit" value="0"  onClick={handleClick} />
    
    </div>

    <div className="icons">
        <FaShoppingCart color="black"/>
    </div>
  

</div> 


    )
}

export default Header;
