import React, {useState} from "react";
import './card.css';
import {FaShoppingBasket} from 'react-icons/fa';
import Globais from "../Globais";


function Card(props) {

    function ModificaQuantidade() {
        Globais.quantidade = Globais.quantidade +1; 
        console.log(Globais.quantidade)   
    }
    

    function AddItem(e) {
       Globais.Item ={
        IdItem: props.id,
        DescItem: props.nome,
        ImgItem: props.src,
        QtdItem: 1,
        ValItem: props.preco

       }

       ModificaQuantidade()
              
    }


    const [qtdItem,setQtdItem] = useState(Globais.quantidade);


useEffect(()=>{
    PegaValor();
  
},[])

    function PegaValor() {
        //Globais.quantidade = Globais.quantidade + 1;
        setQtdItem(Globais.quantidade)
        
    }

    return(<div>
        <div className="header">       
    
    <div className="logos">
        <h1 className="h1Logo">MKS</h1>
        <h3 className="h3Logo">Sistemas</h3>  
        
    </div>
   
    <div className="btn-div">
        <input  className="btn-carrinho"  type="button" value={qtdItem} onClick={PegaValor}/>
  
    
    </div>

    <div className="icons">
        <FaShoppingCart color="black"/>
    </div>
  

</div> 
      <div className="div-principal border">
          
                <img src={props.src} className="card-img-top" alt="..." />
            <div className="txt-price">                
                <p className="card-text">{props.nome}</p>
                <div className="price"><span><a>R$</a>{props.preco}</span></div>
            </div>
         
                <p className="card-subtext">{props.details}</p>
                <input className="btn-comprar" type="button" 
                    value="Comprar" id={props.id} onClick={(e)=>AddItem(e.target.id)}/>
                <div>
                <FaShoppingBasket className="icon" />
                </div>
</div>

</div>   );
    
}

export default Card;
