import React, { useState,useEffect } from "react";
import './carrinho.css';
import Globais from "../Globais";

function Carrinho(){
    const [fechar, setFechar] = useState('');

    useEffect(() => {
      
      console.log(Globais.carrinho);

    });
    
    
    const fecharCarrinho =()=> {
        setFechar('Willian');
        Globais.carrinho=fechar;
        
    }


    return(
        <div className="carrinho">

        <h1 className="titulo">Carrinho<br /> de compras</h1>
        <input className="btn-close" type="button"  value="X" onClick={fecharCarrinho}/>
    
        <p>{'varialevl global:' + Globais.carrinho}</p>

        </div>
    )
}export default Carrinho;
