import React, { useState } from "react";
import './card.css';
import {FaShoppingBasket} from 'react-icons/fa';
import Globais from "../Globais";


function Card(props) {

    const [produtoCart, setProdutoCart] = useState([]);
    
    
    function addProduct(id) {
        setProdutoCart(1)
        const copyItem = [...produtoCart];
        const item = copyItem.map((produto) =>produto.id === id);

        console.log(item);
        

    }
    
    

    function ModificaQuantidade() {
        Globais.quantidade = Globais.quantidade +1; 
        //console.log(Globais.quantidade)   
    }
    

    function AddItem(e) {
       Globais.Item.push = [,{
        IdItem: props.id,
        DescItem: props.nome,
        ImgItem: props.src,
        QtdItem: 1,
        ValItem: props.preco

       }]

       console.log(Globais.Item)

       
              
    }

    return(
        
      <div className="div-principal border">
          
                <img src={props.src} className="card-img-top" alt="..." />
            <div className="txt-price">                
                <p className="card-text">{props.nome}</p>
                <div className="price"><span><a>R$</a>{props.preco}</span></div>
            </div>
         
                <p className="card-subtext">{props.details}</p>
                <input className="btn-comprar" type="button" key={props.key}
                    value="Comprar" id={props.id} onClick={(e)=>addProduct(e.target.id)}/>
                <div>
                <FaShoppingBasket className="icon" />
                </div>
</div>
    );
    
}

export default Card;
