import Carrinho from "../Carrinho/carrinho";
import Card from "../Card/card";
import './body.css'
import axios from "axios";


function Body(props) {
const [list, setList] = useState([]);


useEffect(() => {

    fetch('https://mks-challenge-api-frontend.herokuapp.com/api/v1/products?page=1&rows=8&sortBy=id&orderBy=ASC')
    .then(response => response.json())
    .then(data => setList(data.products))

  },[]);
    

    return(
     <div> 
        <div className="cards">
          
            <div className="grupo-card1">
            {list.map(list =>{
                return(
                    <list key={list.id}>
                        <Card nome={list.name} preco={list.price} details={list.description} src={list.photo}/>    
                            
                    </list>
                )
            })}


                <div className="cards0">
                
                    <Card />
                </div>
            </div>
        </div>
    </div>        

    );


};

export default Body;
