import React, { useEffect, useState } from "react";
import './header.css';
import {FaShoppingCart} from 'react-icons/fa';
import Globais from "../Globais";




function Header() {
const [qtdItem,setQtdItem] = useState(0);

    function teste() {
        console.log(Globais.Item)
        setQtdItem(qtdItem)
        
    }

    useEffect(()=>{
        teste();
    },[Globais.quantidade])

    return(
<div className="header">       
    
    <div className="logos">
        <h1 className="h1Logo">MKS</h1>
        <h3 className="h3Logo">Sistemas</h3>  
        
    </div>
   
    <div className="btn-div">
        <input  className="btn-carrinho"  type="button" value={qtdItem} onClick={()=> setQtdItem (qtdItem +1)}/>
  
    
    </div>

    <div className="icons">
        <FaShoppingCart color="black"/>
    </div>
  

</div> 


    )
}

export default Header;
