import React, { useEffect, useState } from "react";
import './header.css';
import {FaShoppingCart} from 'react-icons/fa';
import Globais from "../Globais";
import Home from "../Home/home";



function Header({showCar}) {
const [qtdItem,setQtdItem] = useState(Globais.Item.length - 1);




function PegaValor(atual){

    if(atual === 'S'){
        setQtdItem(Globais.QtdProdutos);
    }

    if(atual === 'N'){
        showCar('show')

    }
    
    //Globais.Carrinho = true;
    //showCar('show')
    console.log(Globais.Carrinho)
    
}




    return(
<div className="header">       
    
    <div className="logos">
        <h1 className="h1Logo">MKS</h1>
        <h3 className="h3Logo">Sistemas</h3>  
        
    </div>
   
    <div className="btn-div">
    <input  className="btn-carrinho"  type="button" name='btnCar' value={qtdItem} onClick={()=> PegaValor('S')}/>
    
    
    </div>

    <div className="icons">
        <FaShoppingCart color="black"/>
    </div>
  

</div> 


    )
}

export default (Header);
