import React, { useEffect, useState } from "react";
import './card.css';
import {FaShoppingBasket, FaShoppingCart} from 'react-icons/fa';
import Globais from "../Globais";
import { render } from "react-dom";
import Header from "../Header/header";


function Card(props) {

    const [produtoCart, setProdutoCart] = useState([{}]);
    const [qtdItem,setQtdItem] = useState(Globais.Item.length);
    const [verificaItem, setVerificaItem] = useState([{}]);


    useEffect(()=>{
        let qntItens = Globais.Item.length - 1;
    
        setQtdItem(qntItens);
    
    
    },[Globais.Item]);


   
    
    
    function addProduct(e) {

        const copyItem = [...produtoCart];
       
        const item = copyItem.find((produto) =>produto.id === e);

        if(!item){
            copyItem.push({id:e, qtd:1})

           
        }else{
            item.qtd = item.qtd +1
        }
        
        //setProdutoCart(copyItem);
        console.log(produtoCart);

    }
    
    

    function ModificaQuantidade() {
        Globais.quantidade = Globais.quantidade +1; 
        //console.log(Globais.quantidade)   
    }
    

    function AddItem(e) {
        const array1 = [1, 2, 3];

        let tes = array1.includes(4)

        console.log(array1.includes(2));


        const copyItem = [...Globais.Item]

        //const item = copyItem.find((produto) =>produto.id === e);
        const item = copyItem.includes(e);

        //if(!item){
         if(item===false){ 
            Globais.Item.push([{
                IdItem: props.id,
                DescItem: props.nome,
                ImgItem: props.src,
                QtdItem: 1,
                ValItem: props.preco}])
                
           
        }else{

            Globais.item.QtdItem = Globais.item.QtdItem + 1; 
            
        }

    

       console.log(Globais.Item)

       
              
    }

    return(
        <>
      <div className="div-principal border">
          
                <img src={props.src} className="card-img-top" alt="..." />
            <div className="txt-price">                
                <p className="card-text">{props.nome}</p>
                <div className="price"><span><a>R$</a>{props.preco}</span></div>
            </div>
         
                <p className="card-subtext">{props.details}</p>
                <input className="btn-comprar" type="button" key={props.key}
                    value="Comprar" id={props.id} onClick={(e)=>AddItem(props.id)}/>
                <div>
                <FaShoppingBasket className="icon" />
                </div>
  

                
</div>


</>
    );
    
}

export default Card;
