import React, { useState,useEffect } from "react";
import './carrinho.css';
import Globais from "../Globais";


function Carrinho({noShow}){
    const [fechado, setFechado] = useState('');
 
    
    useEffect(()=>{
        if (fechado==='S') {
            Globais.Carrinho=true;
            alert(Globais.Carrinho);
        }else{
            Globais.Carrinho=false;
            alert(Globais.Carrinho);
        } 
    },[])    


    return(
    <div className="carrinho">
        <div className="">
            <h1 className="titulo">Carrinho<br /> de compras</h1>
            <input className="btn-close" type="button"  value="X" onClick={()=>setFechado('S')}/>
        </div>

        <div>
            
        </div>
    </div>
    )
    
}export default Carrinho;
