import React, { useEffect, useState } from "react";
import './header.css';
import {FaShoppingCart} from 'react-icons/fa';
import Globais from "../Globais";




function Header() {
const [qtdItem,setQtdItem] = useState(Globais.quantidade);


useEffect(()=>{
    PegaValor();
  
},[Globais.quantidade])

    function PegaValor() {
        useEffect(()=>{
            let qntItens = Globais.Item.length - 1;
            setQtdItem(qntItens);
        },[Globais.Item]);
        
    }


    return(
<div className="header">       
    
    <div className="logos">
        <h1 className="h1Logo">MKS</h1>
        <h3 className="h3Logo">Sistemas</h3>  
        
    </div>
   
    <div className="btn-div">
        <input  className="btn-carrinho"  type="button" value={qtdItem} onClick={PegaValor}/>
  
    
    </div>

    <div className="icons">
        <FaShoppingCart color="black"/>
    </div>
  

</div> 


    )
}

export default Header;
